package dowhile;
import java.util.*;
public class Patternlefttriange {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i,j,n = 6;
		for(i=0;i<n;i++)
		{
		for(j=2*(n-i);j>=0;j--)
		{
			System.out.print(" ");
		}
		for(j=0;i<=i;j++)
		{
		System.out.println();

	}
		}
	}
}
